mapper.select:
select a.comment_id commentId,a.question_id questionId,a.index_id indexId, a.user_id userId,b.username username,a.content_msg contentMsg,a.quoto_id quotoId, a.re_id reId,a.create_time createTime from et_comment a,et_user b where a.user_id=b.id and a.question_id=#{questionId}
and a.index_id <= #{indexId}
order by a.create_time desc
